precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec2 p = (gl_FragCoord.xy * 2. - r) / r.y / .3;
  gl_FragColor = vec4(fract(p), 0., 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec2 p = (gl_FragCoord.xy * 2. - r) / r.y / .3, v;
  vec4 o = vec4(0.);
  for(float i = 0.; i < 10.; i++) {
    v = p;
    for(float f = 0.; f < 9.; f++) v += sin(v.yx * f + i + t) / f;
    o += (cos(i + vec4(0., 1., 2., 3.)) + 1.) / 6. / length(v);
  }
  gl_FragColor = vec4(tanh(o * o).rgb, 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec2 p = (gl_FragCoord.xy * 2. - r) / r.y;
  float d = .9 - dot(p, p);
  gl_FragColor = vec4(vec3(d), 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec2 p = (gl_FragCoord.xy * 2. - r) / r.y;
  float d = .9 - dot(p, p);
  vec4 o = tanh(.3 / abs(tan(p.y * sqrt(max(d, -d * .1) / .3) + p.x + t + vec4(6., 0., 9., 0.))));
  gl_FragColor = vec4(o.rgb, 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec2 p = (gl_FragCoord.xy * 2. - r) / r.y / .3;
  for(float f = 0.; f < 50.; f++) p += .02 * sin(p.yx + .5 * t);
  gl_FragColor = vec4(fract(p), 0., 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec2 p = (gl_FragCoord.xy * 2. - r) / r.y / .3;
  vec4 o = vec4(0.);
  for(float f = 0.; f < 100.; f++) {
    p += .02 * sin(p.yx + .5 * t);
    o += (cos(f / 27. + vec4(0., 1., 3., 0.)) + 1.1) / length(sin(p * sin(r + f) / .7));
  }
  gl_FragColor = vec4(tanh(o * o / 4e4).rgb, 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec3 rd = normalize(vec3(gl_FragCoord.xy * 2. - r, r.y));
  gl_FragColor = vec4(rd * .5 + .5, 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec4 o = vec4(0.);
  for(float i = 0., z = 0., d; i < 55.; i++) {
    vec3 p = z * normalize(vec3(gl_FragCoord.xy * 2. - r, r.y));
    d = 0.;
    for(float j = 0.; j < 9.; j++) {
      p += sin(ceil(p / 9.).zxy + t);
      d++;
    }
    z += d = .1 * abs(p.z + 40.);
    o += vec4(9., 4., 2., 1.) / d;
  }
  gl_FragColor = vec4(tanh(o / 6e3).rgb, 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec4 o = vec4(0.);
  for(float i = 0., z = 0., d; i < 77.; i++) {
    vec3 p = z * normalize(vec3(gl_FragCoord.xy * 2. - r, r.y));
    p.xy += t;
    d = 0.;
    for(float j = 0.; j < 9.; j++) {
      p += sin(floor(p + .5) + d * 3.).zxy;
      d++;
    }
    z += d = abs(p.z / 30. + .2);
    o += vec4(z, z, 9., 1.) / d;
  }
  gl_FragColor = vec4(tanh(o / 8e4).rgb, 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec2 u = (r - 2. * gl_FragCoord.xy) / r.y;
  vec2 c = ceil((u * 1e2 + sin(u * 3e2 - t)) / (.1 + length(u)));
  vec2 p = u + .01 * tan(dot(c, sin(c.yx)));
  vec4 o = tanh(tanh(vec4(p - .3, p)) * .1 / abs(length(p) - .6));
  gl_FragColor = vec4(o.rgb, 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec2 p = (gl_FragCoord.xy * 2. - r) / r.y;
  float l = 1. - length(p);
  vec4 o = tanh((1.1 + sin(p.x + t + vec4(0., 2., 4., 0.))) / 2e2 / max(l, -l * .1));
  gl_FragColor = vec4(o.rgb, 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec2 p = abs(gl_FragCoord.xy / r - .5);
  vec4 o = p.xyxy / p.yxyx / vec4(3., 6., 9., 1.);
  o = tanh(o + .1 * length(o));
  gl_FragColor = vec4(o.rgb, 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec2 p = (gl_FragCoord.xy * 2. - r) / r.y;
  p *= max(1. - dot(p, p), .1) / .1;
  vec4 o = sin(sin(p.xyxy) + p.yxyx + t) / vec4(-2., 2., 2., 2.) + .5;
  gl_FragColor = vec4(o.rgb, 1.);
}

precision mediump float;
uniform float t;
uniform vec2 r;
void main() {
  vec2 p = gl_FragCoord.xy / r.y / .1;
  for(int i = 0; i < 20; i++) p += cos(p.yx);
  gl_FragColor = vec4(sin(p.x), sin(p.x + 2.), sin(p.x + 4.), 1.);
}
